package CommonInterviewQuestions_Naveen;

public class NumberIsPositiveOrNegitive {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		double num =12.5;
		
		if(num>0) {
			
			System.out.println(num+" The number is positive");
		}else if(num<0) {
			
			System.out.println(num+" The number is Negitive");
		}else {
			System.out.println(num+" The number is Zero");
		}

	}

}
