public class PrintEven_Odd_NumbersInArray {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
     int a[] = { 5,4,9,2,1};
     
     for(int  value:a) {
    	 
    	 if(value%2==0) {
    		 
    		 System.out.println(" Print Even Numbers in Array ::"+value);
    	 }else {
    		 
    		 System.out.println("Print Odd Number in Array::"+value);
    	 }
    	 
    	 } 
    		 
    		 
    	 }
    	 
     }
     
     
		
		


